    private void generateNewStaffID(){
        newStaffID = "STF001";
        String lastStaffID = null;
            try (BufferedReader br = new BufferedReader(new FileReader(f))){
                String line;
                while ((line = br.readLine()) != null){
                    lastStaffID = line.split(",")[0];//Assume Staff ID is the first value
                }
            }catch (IOException e){
                JOptionPane.showMessageDialog(this, "Error reading file: " + e.getMessage());
            }
            
            int number;
            if (lastStaffID != null){
                number = Integer.parseInt(lastStaffID.substring(3));//Extract the number part
                newStaffID = String.format("STF%03d", number + 1 );//Increment and format
            }
    }
