    private void SignUpButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SignUpButton1ActionPerformed
        StaffIDTextField1.setText(newStaffID);
        Username = UsernameTextField2.getText().toUpperCase();
        char[] password = jPasswordField1.getPassword();
        
        if (Username.isEmpty() || password.length == 0){
            JOptionPane.showMessageDialog(this, "Please fill in your information details");
            return;
        }

        if (Username.isEmpty()){
            JOptionPane.showMessageDialog(this, "Username cannot be empty!");
            return;
        }
        if (password.length == 0) {
            JOptionPane.showMessageDialog(this, "Password cannot be empty");
            return;
        }
        if (password.length < 8){
            JOptionPane.showMessageDialog(this, "Password must be 8 character!");
            return;// Prevent further execution
        }
        
        generateNewStaffID();//Generate or refresh Staff ID
        StaffIDTextField1.setText(newStaffID);
        
        try{
            //Check if lecturerinfo.txt exists
            //if lecturerinfo.txt does not exists, create one
            if (!f.exists()){
                f.createNewFile();
            }
            
            //Check if Staff ID already exists
            try(BufferedReader br = new BufferedReader(new FileReader(f))){
                //Read the file to check if StaffID exists
                String eachLine;

                while((eachLine = br.readLine()) != null){
                    String values[] = eachLine.split(",");
                    if (values[1].equals(Username)){
                        JOptionPane.showMessageDialog(this, "Username already exists. Please try with another Username.");
                        StaffIDTextField1.setText(newStaffID);
                        UsernameTextField2.setText("");
                        jPasswordField1.setText("");
                        return;
                    }
                }  
                
            } catch (IOException e){
                JOptionPane.showMessageDialog(this, "An error occured while checking the Staff ID");
                return;
            }
            
            //write data to the file 
            try (FileWriter fw = new FileWriter(f, true); BufferedWriter bw = new BufferedWriter(fw)){
                String passwordStr = new String(password);
                bw.write(newStaffID+","+Username+","+passwordStr+"\n");
                bw.close();
            }catch (IOException e) {
                JOptionPane.showMessageDialog(this, "An error occurred while writing to the file.");
                return;
            }
            
            //display welcome message
            JOptionPane.showMessageDialog(this, "Registered successfully! Your Staff ID is "+newStaffID+". Please Log In now.");
            LecturerLogin ll = new LecturerLogin();
            ll.setVisible(true);
            this.dispose();
            
            //clear the text fields after sign up
            StaffIDTextField1.setText(newStaffID);
            UsernameTextField2.setText("");
            jPasswordField1.setText("");
        } catch (IOException e){
            JOptionPane.showMessageDialog(this, "An error occured while writing to the file.");
        }
    }//GEN-LAST:event_SignUpButton1ActionPerformed

